sys_script_include.script:
function validateEmailAddress(emailStr){
	//Use JavasScript coercion to guarantee emailStr is a string
	emailStr=emailStr+'';
	//compare emailstr against the allowed syntax as specified in the regular expression
	//if emailstr has allowed syntax, return true, else retuen false
	if(emailStr.match(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/))
	{
		return true;
	}
	else{
		return false;
	}
}
